#!/usr/bin/env bash

function _check() {
	printf '%s - %s\n\n%s' "${1}" "${3}" "$(am ${2} || FAIL: ${1})"
}



clear
printf '\n----------------------------------------------------------------------\n\n\t\tAM testing started\n\n----------------------------------------------------------------------\n\n'
_check am am 'without argument'
printf '\n----------------------------------------------------------------------\n\n'
_check version version 'show version'
printf '\n----------------------------------------------------------------------\n\n'

printf '\n----------------------------------------------------------------------\n\n'

printf '\n----------------------------------------------------------------------\n\n'

printf '\n----------------------------------------------------------------------\n\n'

printf '\n----------------------------------------------------------------------\n\n'

printf '\n----------------------------------------------------------------------\n\n\t\tAM testing Finished\n\n----------------------------------------------------------------------\n\n'

exit 0
